package javaloops;
//write a java program to print even numbers in given in-between numbers

public class Example7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int number=200;
System.out.println("list of even numbers from 101 to "+number+":");
for(int i=101;i<=number;i++)
{
	if(i%2==0)
	{
		System.out.println(i+ "");
	}
}
	}

}
